mapper.select:
select id, meeting_no, topic, begin_time, end_time, location, on_off, deleted, create_time, update_time, create_by, update_by
from video_meeting.meeting


        topic like '%${filter.topic}%'


        and begin_time > #{filter.searchTimeStart,jdbcType=DATE}


        and begin_time < #{filter.searchTimeEnd,jdbcType=DATE}
